INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (100, 51, 'IBM ThinkPad A31p', 'Непревзойдённый модельный ряд мобильных супер-компьютеров с утончённым дизайном для работы пользователя в любых условиях! Ноутбуки IBM ThinkPad - это революционный дизайн сверхлегкого корпуса без ущерба для вычислительной мощности, функциональности и коммуникаций. Приобретая новый ноутбук все-в-одном IBM ThinkPad A Series, Вы получаете полноценную альтернативу настольному компьютеру и возможность выбора из широкого ряда моделей: от недорогих ноутбуков начального уровня до высокопроизводительных ноутбуков с непревзойденными графическими возможностями. Ощутите всю мощь новых процессоров Intel Celeron и Intel Pentium III и Intel Pentium 4. Полноценная замена настольных компьютеров, ноутбуки "все-в-одном" IBM ThinkPad A Series, смогут существенно улучшить и в то же время упростить Ваши мобильные вычисления. Вы получите в свое распоряжение высокоэффективные графические возможности, доступ в Интернет путем нажатия всего лишь одной клавиши и поддержку возможности беспроводного соединения с другими устройствами. К тому же все опции к A Series взаимозаменяемы с опциями к новым ноутбукам ThinkPad T Series, так что Вы сможете менять Ultrabay 2000 устройства, память и адаптеры. \r\r\n', '3.75', '2610', '27183.jpg', 11111, '27183_s.jpg', 6, 5, '', 1, 'P-IV 1.7GHz, 15’’ TFT, 256MB,\r\r\n60.0GB, 64MB AGP, DVD-R/CD-RW комбо, 56К,\r\r\n10/100 Eth, wireless, WinXP Pro ', '2870', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (101, 51, 'IBM ThinkPad T30 2366-89G', 'Хочется иметь легкий, мощный, да к тому же престижный ноутбук, подчеркивающий ваш успех в бизнесе, но не знаете, как все это совместить? IBM ThinkPad T30 — вот ответ! Модели ThinkPad Т Series используют яркую 14-дюймовую TFT-матрицу, полноразмерную клавиатуру с возможностью прокрутки и новейшие разработки IBM и Intel для мобильных систем. Компьютеры T Series имеют корпус из титанового композита — материала, позволившего не только облегчить конструкцию, но и придать ей дополнительную прочность и обеспечить великолепный отвод тепла. Разъем UltraPort поможет вам превратить ноутбук в фото- или видеокамеру, подключиться к видеоконференции. Уникальное устройство подсветки клавиатуры ThinkLight™ позволит работать в самолете или автомобиле не испытывая дискомфорта и совместимо с ThinkPad Port Replicator и ThinkPad Dock. Новое позиционирующее устройство UltraNav объединяет хорошо известный манипулятор TrackPoint и настраиваемую сенсорную панель TouchPad, углы которой могут использоваться для быстрого запуска приложений или перехода к избранным web-сайтам. К тому же, все опции к T Series взаимозаменяемы с опциями к новым ноутбукам ThinkPad A Series, так что вы сможете менять устройства Ultrabay 2000, память и адаптеры. Включение в корпус титановых композиционных материалов делает ThinkPad T Series невероятно прочным, а дополнительный встроенный амортиза. \r\r\n', '0', '3170', '39947.jpg', 11111, '39947_s.jpg', 0, 0, '', 1, 'P-IV 2000GHz, 14.1’’ TFT, 256MB,\r\r\n40.0GB, 16MB AGP, DVD-ROM, 56К, 10/100 Eth,\r\r\nwireless, WinXP Pro ', '3400', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (102, 51, 'Toshiba Tecra 9100', 'P-IV 1.4GHz, 14.1’’ TFT, 256MB,\r\r\n20GB, 16MB AGP, DVD-ROM, 56К, 10/100 Eth,\r\r\nwireless, WinXP Pro \r\r\n<p>\r\r\nКомпания Toshiba предлагает большой выбор разнообразных дополнительных устройств и аксессуаров к своим ноутбукам семейств Portege, Satellite, Satellite Pro и Tecra. В зависимости от ваших потребностей вы можете выбрать дополнительные модули памяти, жесткие диски, приводы FDD, CD-ROM, CD-RW, DVD-ROM, модемы и сетевые карты PCMCIA, порт-репликаторы, стыковочные модули, дополнительные аккумуляторные батареи, периферию, а также специальные сумки для ноутбуков. Использование этих устройств и аксессуаров позволит вам не только повысить эффективность работы с ноутбуками Toshiba, но и сделает ее намного удобнее. \r\r\n', '0', '2310', '28187.jpg', 11111, '28187_s.jpg', 0, 2, '', 1, 'P-IV 1.4GHz, 14.1’’ TFT, 256MB,\r\r\n20GB, 16MB AGP, DVD-ROM, 56К, 10/100 Eth,\r\r\nwireless, WinXP Pro ', '2571', '');
