INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (104, 58, 'IBM NetVista M42', 'Модель NetVista M42 - сегодня самая производительная среди настольных компьютеров IBM. Она также выполнена на базе процессора Intel Pentium 4 и снабжена шестью USB-портами версии 2.0. Этот компьютер будет поставляться в четырех различных корпусах, включая Mini Desktop с небольшой площадью опоры и возможностью горизонтальной либо вертикальной ориентации блока. В некоторые модификации M42 встроен адаптер Gigabit Ethernet для подключения системы к высокоскоростным сетям. \r\r\n', '0.1', '1152', '42623.jpg', 0, '42623_s.jpg', 1, 0, '', 1, 'P-IV 2.0GHz, 256MB PC2100 DDR SDRAM, 40GB,\r\r\nCD-ROM, AGP, 10/100 Eth, WinXP Pro ', '0', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (105, 58, 'HP Compaq Evo D310', 'Настольные ПК Evo DESKTOP серии D300 - можно охарактеризовать девизом "Все что нужно и ничего лишнего". Эта машина выпускается в различных вариантах корпуса и предназначена для удовлетворения потребностей пользователей в обеспечении всех необходимых деловых манипуляций по доступной цене. Compaq EVO D310 в исполнении Micro Desktop – это удобный дизайн корпуса, наиболее полный набор функций настольного ПК, при этом очень привлекательная цена. \r\r\n', '2.96667', '1207', '41772.jpg', 0, '41772_s.jpg', 3, 0, '', 1, 'P-IV 2.4GHz, 256MB PC2100 DDR SDRAM, 40GB,\r\r\nCD-RW, Direct AGP, 10/100 Eth, WinXP Pro ', '1300', '');
