INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (107, 21, 'Duo MP3', 'Очередной представитель некоего гибридного направления MP3 плееров, способных, в то же время, вдохнуть новую жизнь в старые добрые проигрыватели для магнитных кассет - DUO-Aria от Digisette. Способен работать как сам по себе, так и обычной кассетой для обычного магнитофона. USB интерфейс, поддержка MP3 и WMA, встроенные 32 Мбайт памяти и слот для MultiMediaCard (еще +64 Мбайт). Не оснащен LCD экраном, но он доступен в качестве подключаемой опции. \r\r\n', '2.475', '170', '37598.jpg', 0, '37598_s.jpg', 4, 0, '', 1, 'Очередной представитель некоего гибридного направления MP3 плееров, способных, в то же время, вдохнуть новую жизнь в старые добрые проигрыватели для магнитных кассет.', '0', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (108, 72, 'Стив Круг :: Веб-дизайн: `не заставляйте меня думать`', 'Люди не станут пользоваться сайтом, если им не будет понятно, как по нему перемещаться. Судьбы компаний сегодня во многом зависят от их сайтов. Эти крупнобюджетные проекты возглавляют люди, зачастую не имеющие никакого или, по крайней мере, достаточного опыта в вопросах юзабилити, которые неожиданно приобрели важное, решающее значение. Данная книга написана для тех, кто занимается практической работой - дизайнеров, программистов, вебмастеров, менеджеров, специалистов по маркетингу, а также для тех, кто подписывает чеки. В ясной и простой форме автор описывает принципы успешного веб-дизайна. Руководствуясь ими, вы сможете задавать своим экспертам правильные вопросы, а также получать практические ответы, которые позволят находить решения для трудных технических, эстетических и структурных проблем разработки сайтов. ', '0', '8', '10805.jpg', 2515, '10805s.jpg', 0, 1, '', 1, 'Издание продолжает известную серию "Библиотека веб-дизайнера", в которой уже вышли супербестселлеры гуру веб-дизайна: ...\r\r\n', '8.6', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (109, 23, 'Туманы Авалона', 'Эта полная мужества, любви и печали повесть рассказывает о тех годах, когда мир бога христиан еще не вытеснил с полотна времен мир фэйри, и магия была делом обыденным. Когда король Артур, возведенный на трон Владычицей Авалона, нарушил клятву и тем самым раз и навсегда изменил судьбу Британии, когда Фату Моргану еще называли просто Моргейной, герцогиней Корнуолла, и не считали злой колдуньей и когда тайное течение рек жизни еще подчинялось воле посвященных. Теперь все думают, что это легенды, сложенные сладкоречивыми бардами в надежде на кров и благоволение королей. Но это все было. И вот как было оно. \r\r\nРоман "Туманы Авалона" американские и европейские любители фантастики ставят в один ряд с `Властелином Колец` Дж.Р.Р.Толкиена. Теперь и у российских читателей есть возможность убедиться, справедливо ли это. \r\r\n', '4.7', '5.4', '81446.jpg', 0, '81446s.jpg', 8, 0, '', 1, 'Эта полная мужества, любви и печали повесть рассказывает о тех годах, когда мир бога христиан еще не вытеснил с полотна времен ...\r\r\n', '0', '');
