INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (113, 73, 'Visual C++ и MFC', 'Книга посвящена объектно-ориентированному программированию для 32-разрядных систем Windows 95/98 и Windows NT с использованием компилятора Visual C++ 6 и библиотеки классов MFC версии 4.23. Книга содержит исчерпывающую информацию о библиотеке классов MFC (Microsoft Foundation Classes) и методах создания приложений Windows на основе классов этой библиотеки, а также большое количество наглядных и подробно прокомментированных примеров приложений, полные тексты которых приведены на сопроводительной дискете. Для программистов, владеющих языками С и С++ и имеющих опыт программирования в системе Windows \r\r\n', '0', '17', '4064.jpg', 1245, '4064s.jpg', 0, 6, '', 1, 'Книга посвящена объектно-ориентированному программированию для 32-разрядных систем Windows 95/98 и Windows NT с использованием', '0', '');
INSERT INTO SS_products (productID, categoryID, name, description, customers_rating, Price, picture, in_stock, thumbnail, customer_votes, items_sold, big_picture, enabled, brief_description, list_price, product_code) VALUES (114, 73, 'Профессиональное PHP программирование', 'Эта книга о программировании на PHP - языке сценариев, выполняющихся на стороне сервера для создания динамических веб-страниц. Его можно использовать совместно со многими технологиями, и в этой книге вы найдете подробности об интеграции PHP с базами данных, LDAP, XML и почтовым сервером IMAP. В книге вы найдете полное описание PHP: от инсталляции на сервере и конфигурирования интерпретатора языка PHP до примера проектирования сложного динамического приложения. Кроме того, рассматривается работа с базами данных и каталогами, с динамической графикой и XML. Хотя большая часть книги посвящена PHP3, информацию о новых возможностях, предоставляемых PHP4, вы тоже найдете.\r\r\nКнига написана профессиональными программистами для разработчиков, которые хотят достичь подлинного мастерства в осуществлении своих веб-проектов. Специалистам в области CGI-программирования быстро развивающаяся и набирающая популярность технология PHP предоставит скорость и удобство разработки. Предполагается, что у читателя есть некоторые базовые познания web-технологий. Он должен быть в состоянии работать с HTML, иметь опыт создания программ на каком-либо из многочисленных языков сценариев или программирования и некоторое представление о том, как работает система распределенной информации в Сети. Предполагается также наличие знакомства хотя бы с принципами работы баз данных. Книга является признанным учебником по данному предмету, хорошо известна среди специалистов и имеет весьма положительные отзывы. ', '0', '15', '8472.jpg', 1235312, '8472s.jpg', 0, 10, '', 1, 'Эта книга о программировании на PHP - языке сценариев, выполняющихся на стороне сервера для создания динамических веб-страниц.', '18', '');
